package com.first.lecture.ch03;

public class DenyLogicOperatorExample {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		boolean play = true;
		/*
		 * boolean play1 =false;
		 * 
		 * boolean useNy = true; boolean delNy = false;
		 */
		
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
		
		play = !play;
		System.out.println(play);
		
		
	}

}
